// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

// interface ITuner {
// function balanceOf(address smartAccount) external view returns (uint);
// }

contract Paymaster {
    address public immutable entryPoint;
    //address public immutable token;
    address public immutable owner;

    mapping(address => bool) public whiteList;

    uint constant TOKEN_AMOUNT = 10 ** 18;
    uint constant MAX_COST = 0.01 ether;

    constructor(address _entryPoint) {
        entryPoint = _entryPoint;
        owner = msg.sender; // paymaster 컨트랙트 배포자가 owner
    }

    modifier onlyEntryPoint() {
        require(msg.sender == entryPoint);
        _;
    }

    modifier onlyOwner() {
        require(msg.sender == owner);
        _;
    }

    function addWhiteList(address smartAccount) external onlyOwner {
        whiteList[smartAccount] = true;
    }

    function removeWhiteList(address smartAccount) external onlyOwner {
        whiteList[smartAccount] = false;
    }

    function validatePaymasterUserOp(
        address smartAccount,
        uint amount
    ) external view onlyEntryPoint returns (bool) {
        // uint balance = ITuner(token).balanceOf(smartAccount);
        // require(balance >= TOKEN_AMOUNT);
        require(whiteList[smartAccount], "You're not on whiteList");
        require(amount <= MAX_COST);
        return true;
    }
}

contracts/AA/SmartAccount.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

contract SmartAccount {
    address owner; // EOA 주소?
    address entryPoint;

    constructor(address _owner, address _entryPoint) {
        owner = _owner;
        entryPoint = _entryPoint;
    }

    modifier onlyEntryPoint() {
        require(msg.sender == entryPoint);
        _;
    }

    modifier onlyOwner(address _owner) {
        require(owner == _owner);
        _;
    }

    function execute(
        address to,
        uint value,
        bytes calldata data
    ) external onlyEntryPoint {
        (bool success, ) = to.call{value: value}(data);
        require(success);
    }

    function isValidSign(
        bytes32 _hash,
        bytes calldata sign
    ) external view returns (bool) {
        return _recoverSigner(_hash, sign) == owner;
    }

    function _recoverSigner(
        bytes32 _hash,
        bytes memory sign
    ) internal pure returns (address) {
        (uint8 v, bytes32 r, bytes32 s) = _splitSign(sign);
        return ecrecover(_hash, v, r, s);
    }

    function _splitSign(
        bytes memory sign
    ) internal pure returns (uint8 v, bytes32 r, bytes32 s) {
        require(sign.length == 65);
        assembly {
            v := byte(0, mload(add(sign, 96)))
            r := mload(add(sign, 32))
            s := mload(add(sign, 64))
        }

        if (v < 27) {
            v += 27;
        }

        require(v == 27 || v == 28);
    }

    receive() external payable {}
}

